/**
 * Wrap ensures an object is an array.
 * ```
 *     wrap("str")   => ["str"]
 *     wrap(["str"]) => ["str"]
 * ```
 */
export declare const wrap: <T>(object: T | T[]) => T[];
/**
 * Loops over each array position, runs the callback with the corresponding items, and collects the results.
 */
export declare const mapPairs: <Left, Right, Result>(left: Left[], right: Right[], fn: (left: Left, right: Right) => Result) => Result[];
